app/pods/application/template.hbs.source:
{{#if (not inError)}}
    <main class="page-content" data-testing-id="page-content">
      {{#each flashMessages.queue as |flash|}}
        {{#if flash.componentName}}
          {{component flash.componentName content=flash.content}}
        {{else}}
          <FlashMessage @flash={{flash}} @messageStyle="foundation" @class="text-center" />
        {{/if}}
      {{/each}}
      {{#if uiElements.header}}
        <HeaderBar @logOut={{action 'logOut'}}
                   @login={{action 'logIn'}}
                   @inLogin={{inLogin}}
                   @isBrandsRedesign={{model.isBrandsRedesign}}/>
        {{#if (and (not uiElements.holidayTimer) winbackPromotion.shouldSeeBannerApplication)}}
          <WinbackBanner/>
        {{/if}}
      {{/if}}
      {{outlet}}
    </main>
    {{component 'modal-container'}}
  {{else}}
    {{outlet "error"}}
  {{/if}}

app/pods/brands/template.hbs.source:
{{#if showSideMenu}}
  {{#if winbackPromotion.shouldSeeBannerFreemium}}
    <WinbackBanner/>
  {{else if uiElements.holidayTimer}}
    <HolidayBanner/>
  {{/if}}

  {{#if model.isBrandsRedesign}}
    <div class="flex flex-col flex-grow md:mb-xl md:mx-xl">
      <div class="relative flex">
        <h2 class="ttext-title text-center my-sm w-full">{{t 'account.all_brands'}}</h2>
        <div class="hidden md:flex absolute right-0 self-center">
          <a href={{homePage}} class="tbtn plus tailor-primary-gradient rounded-full" data-testing-id="create-new">{{t 'account.new_brand'}}</a>
        </div>
      </div>
      <div class="flex justify-center md:bg-white">
        <BrandsTopNav/>
      </div>
      <div class="bg-white flex-grow">
        {{outlet}}
      </div>
    </div>
  {{else}}
    <div class="flex flex-grow-1">
      <div class="show-for-medium">
        <BrandsSideNav @menuLinks={{menuLinks}}/>
      </div>
      <div class="flex-grow-1 md:px-xl">
        {{outlet}}
      </div>
    </div>
  {{/if}}
{{else}}
  {{outlet}}
{{/if}}

