app/pods/components/asset-download/templ.source:
{{#if (not permitted)}}
  <div class="asset-download-section wave-bg {{type}}">
    <div class="ttext-subtitle mb-xs p-sm">
      {{t (concat 'brands.brand_version.download.' title)}}
      <div class="section-content">
        {{#if loading}}
          <div class="loading">
            <BouncingLoader/>
          </div>
        {{/if}}
        <div class="upgrade-widget center">
          <div class="content {{if (or (eq type 'logo-size') (eq type 'printable')) 'wide'}}">
            <div class="ttext-page {{if (not (eq type 'printable')) 'mb-sm'}}">
              {{t (concat 'brands.brand_version.download.freemium_brand_files.upgrade_widget.' assetClass)}}
            </div>
            {{#if (eq type 'printable')}}
              <div class="printable-action flex">
                <LinkTo @route="brands.brand-version.print.printable"
                        @data-testing-id={{concat 'premium-templates-' type}}
                        @query={{hash backTo='downloads'}}
                        @class="tbtn blue {{if (not deviceTypes.isPhone) 'outline'}} rounded-full mr-ms mt-ms {{if (not hideUpgradeSymbol) 'crown'}}">
                  {{t 'brands.brand_version.print.business-cards.cta_premium'}}
                </LinkTo>
                <button type="button" {{action 'redirectToZazzle'}}
                   data-testing-id={{concat 'free-templates-' type}}
                   class="tbtn blue outline rounded-full {{if (not logoPublicPath) 'no-clicks' 'next'}} mt-ms">
                  {{#if logoPublicPath}}
                    {{t 'brands.brand_version.print.business-cards.cta'}}
                  {{else}}
                    <BouncingLoader @class="button blue-dots"/>
                  {{/if}}
                </button>
              </div>
            {{else if (eq type 'business-card')}}
              <LinkTo @route="brands.brand-version.print-store" @class="tbtn blue outline rounded-full my-xs">
                {{t 'print_store.print_page.cta'}}
              </LinkTo>
            {{else if (eq type 'logo-size')}}
              <LogoSizeDownloads class="md:m-xs"
                @primaryBrandVersion={{primaryBrandVersion}}
                @logoSizesFree={{logoSizesFree}}
                @onLogoSizeSelected={{onLogoSizeSelected}}>
              </LogoSizeDownloads>
            {{else}}
              <button class="tbtn crown blue {{if (not deviceTypes.isPhone) 'outline'}} rounded-full"
                      data-testing-id="downloads_page_{{type}}_download_crown_button" {{action 'openUpgradeModal'}} type="button">{{t 'download'}}</button>
            {{/if}}
          </div>
        </div>
      </div>
    </div>
    {{#if (eq type 'logo-size')}}
      <div class="logo-size-strip"/>
    {{else}}
      <div class="click-action" role="button" {{action 'clickAction'}}>
        <AssetMockup @type={{type}}
                    @splashClass='brand-files'
                    @mockupCopies={{mockupCopies}}
                    @imgHeight={{11}}
                    @openUpgradeModal={{action 'openUpgradeModal'}}
                    @src={{concat '/images/components/asset-download/asset_' type '.png'}}
                    @mockupUrl={{primaryBrandVersion.mockupUrl}}/>
      </div>
    {{/if}}
  </div>
{{else if (or items loading)}}
  <div class="asset-download-section {{type}}">
    <div class="ttext-subtitle mb-xs">
      {{t (concat 'brands.brand_version.download.' title)}}
      <div class="section-content {{if loading 'flex'}}">
        {{#if loading}}
          <div class="loading my-md">
            <BouncingLoader/>
          </div>
        {{/if}}
        {{#if items}}
          <div class="flex align-items-start flex-wrap">
            {{#each items as |asset|}}
              {{#if asset}}
                {{#if asset.mockupUrl}}
                  <AssetCard @asset={{asset}}
                             @imgSrc={{asset.mockupUrl}}
                             @title={{if asset.subtitle asset.subtitle asset.formattedUpdatedAt}}
                             @loadingMessage={{t 'asset_creating_message'}}>
                    <AssetCardButtons @asset={{asset}}
                                      @country={{country}}
                                      @primaryBrandVersionUpdated={{updateBrandVersion}}/>
                  </AssetCard>
                {{else if (eq asset.assetType 'vector-logo')}}
                  <AssetCard @asset={{asset}}
                             @topClass="brand-image-triangle"
                             @imgSrc={{asset.brandVersion.mockupUrl}}
                             @bgColor={{asset.brandVersion.color}}
                             @title={{if asset.subtitle asset.subtitle asset.formattedUpdatedAt}}
                             @loadingMessage={{t 'asset_creating_message'}}>
                    <AssetCardButtons @asset={{asset}}
                                      @primaryBrandVersionUpdated={{updateBrandVersion}}/>
                  </AssetCard>
                {{else if (eq asset.assetType 'seasonal-logo')}}
                  <AssetCard @asset={{asset}}
                             @topClass={{concat asset.assetType ' brand-image-triangle'}}
                             @imgSrc={{asset.brandVersion.mockupUrl}}
                             @title={{t (concat 'brands.brand_version.download.holidays.' asset.eventName)}}
                             @loadingMessage={{t 'asset_creating_message'}}>
                    <AssetCardButtons @asset={{asset}}
                                      @pageAction={{action 'pageAction'}}
                                      @primaryBrandVersionUpdated={{updateBrandVersion}}/>
                  </AssetCard>
                {{else if (eq asset.assetType 'business-card')}}
                  <AssetCard @asset={{asset}}
                             @imgSrc={{asset.frontSideDownloadUrl}}
                             @loadingMessage={{t 'asset_creating_message'}}>
                    <AssetCardButtons @asset={{asset}}
                                      @primaryBrandVersionUpdated={{updateBrandVersion}}/>
                  </AssetCard>
                {{else if (eq asset.assetType 'logo')}}
                  {{#if asset.brandVersion.primary}}
                    <AssetCard @asset={{asset}}
                               @imgSrc={{asset.brandVersion.mockupUrl}}
                               @title={{t 'brands.brand_version.download.primary_logo'}}
                               @loadingMessage={{t 'asset_creating_message'}}>
                      <AssetCardButtons @asset={{asset}}
                                        @pageAction={{action 'pageAction'}}
                                        @primaryBrandVersionUpdated={{updateBrandVersion}}
                                        @onAssetDownload={{onAssetDownload}}/>
                    </AssetCard>
                  {{else}}
                    <AssetCard @asset={{asset}}
                               @imgSrc={{asset.brandVersion.mockupUrl}}
                               @title={{asset.formattedUpdatedAt}}
                               @loadingMessage={{t 'asset_creating_message'}}>
                      <AssetCardButtons @asset={{asset}}
                                        @pageAction={{action 'pageAction'}}
                                        @primaryBrandVersionUpdated={{updateBrandVersion}}/>
                    </AssetCard>
                  {{/if}}
                {{else}}
                  <AssetCard @asset={{asset}}
                             @topClass={{concat asset.assetType " brand-image-triangle"}}
                             @imgSrc={{asset.brandVersion.mockupUrl}}
                             @title={{asset.formattedUpdatedAt}}
                             @loadingMessage={{t 'asset_creating_message'}}>
                    <AssetCardButtons @asset={{asset}}
                                      @primaryBrandVersionUpdated={{updateBrandVersion}}/>
                  </AssetCard>
                {{/if}}
              {{/if}}
            {{/each}}
          </div>
          {{#if items.meta}}
            <PaginationNav @pageAction={{action 'pageAction'}}
                           @source={{items}}/>
          {{/if}}
        {{/if}}
      </div>
    </div>
  </div>
{{else}}
  {{#if (eq type 'seasonal-logo')}}
    <div class="asset-download-section {{type}}">
      <div class="ttext-subtitle mb-xs">
      {{t (concat 'brands.brand_version.download.' title)}}
        <div class="flex flex-col">
          <span class="bold ttext">{{t 'brands.brand_version.download.no_seasonal_logos_title'}}</span>
          <span class="ttext">{{t 'brands.brand_version.download.no_seasonal_logos_description'}}</span>
        </div>
      {{#if upcoming}}
        <div class="seasonal-logos-upcoming p-xs mt-sm">
          <span>{{t 'brands.brand_version.download.upcoming'}}</span>
          <span>{{t (concat 'brands.brand_version.download.holidays.' upcoming.event_name)}}</span>
          <span>{{t 'brands.brand_version.download.available_in_days' days=upcoming.days_from_now}}</span>
        </div>
      {{/if}}
      </div>
    </div>
  {{else if (eq type 'printable')}}
    <div class="asset-download-section wave-bg {{type}}">
      <div class="no-printables-container pt-sm pl-sm w-full justify-between">
        <div clas="flex flex-col">
          <div class="ttext-subtitle">{{t (concat 'brands.brand_version.download.' title)}}</div>
          <div class="custom-width mb-xs ttext-page">{{t 'brands.brand_version.print.business-cards.text'}}</div>
          <div class="printable-action flex mb-sm">
            <LinkTo @route="brands.brand-version.print.printable"
                    @data-testing-id={{concat 'premium-templates-' type}}
                    @query={{hash backTo='downloads'}}
                    @class="tbtn blue {{if (not deviceTypes.isPhone) 'outline'}} rounded-full mr-ms mt-ms {{if (not hideUpgradeSymbol) 'crown'}}">
              {{t 'brands.brand_version.print.business-cards.cta_premium'}}
            </LinkTo>
            <button type="button" {{action 'redirectToZazzle'}}
               data-testing-id={{concat 'free-templates-' type}}
               class="tbtn blue outline rounded-full {{if (not logoPublicPath) 'no-clicks' 'next'}} mt-ms">
              {{#if logoPublicPath}}
                {{t 'brands.brand_version.print.business-cards.cta'}}
              {{else}}
                <BouncingLoader @class="button blue-dots"/>
              {{/if}}
            </button>
          </div>
        </div>
        <AssetMockup @type={{type}}
                     @splashClass='brand-files'
                     @mockupCopies={{mockupCopies}}
                     @imgHeight={{11}}
                     @openUpgradeModal={{action 'openUpgradeModal'}}
                     @src={{concat '/images/components/asset-download/asset_' type '.png'}}
                     @mockupUrl={{primaryBrandVersion.mockupUrl}}/>
      </div>
    </div>
  {{else if (eq type 'business-card')}}
    <div class="asset-download-section wave-bg {{type}}">
      <div class="no-printables-container pt-sm pl-sm w-full justify-between">
        <div clas="flex flex-col">
          <div class="ttext-subtitle">{{t (concat 'brands.brand_version.download.printable')}}</div>
          <div class="custom-width mb-xs ttext-page">{{t 'brands.brand_version.print.business-cards.text'}}</div>
          <div class="printable-action flex mb-sm">
            <LinkTo @route="brands.brand-version.print-store" @class="tbtn blue outline rounded-full my-xs">
              {{t 'print_store.print_page.cta'}}
            </LinkTo>
          </div>
        </div>
        <AssetMockup @type="business-card"
                     @splashClass='brand-files'
                     @mockupCopies={{mockupCopies}}
                     @imgHeight={{11}}
                     @openUpgradeModal={{action 'openUpgradeModal'}}
                     @src={{concat '/images/components/asset-download/asset_' type '.png'}}
                     @mockupUrl={{primaryBrandVersion.mockupUrl}}/>
      </div>
    </div>
  {{else}}
    <div class="asset-download-section {{type}}">
      <div class="ttext-subtitle mb-xs">
        {{t (concat 'brands.brand_version.download.' title)}}
        <div class="row align-center">
          <div class="columns small-12 medium-8 my-lg text-center">
            <div class="ttext-jumbo text-center"><i class="icon-binoculars"></i></div>
            <h1 class="ttext-subtitle text-center mb-sm">{{t 'brands.brand_version.download.no_assets'}}</h1>
            {{#if (and assetClass (eq assetClass 'printable'))}}
              <LinkTo @route="brands.brand-version.print.printable"
                      @query={{hash asset_class=assetClass asset_id=null backTo='downloads'}}
                      class="tbtn red py-xs">
                {{t "brands.brand_version.download.create"}}
              </LinkTo>
            {{else if assetClass}}
              <LinkTo @route="brands.brand-version.design.genie"
                      class="tbtn red py-xs">
                {{t "brands.brand_version.download.create"}}
              </LinkTo>
            {{/if}}
          </div>
        </div>
      </div>
    </div>
  {{/if}}
{{/if}}

