app/pods/components/bundles-page/templat.source:
{{#if winbackPromotion.shouldSeeBannerFreemium}}
  <WinbackBanner/>
{{else if shouldSeeHolidayBanner}}
  <HolidayBanner/>
{{/if}}
<div class="bundles-page">
  {{#if this.timer.currentHoliday}}
    <BundlesPage::HolidayPlansHeader @brandVersion={{brandVersion}}/>
  {{else}}
    <BundlesPage::PlansHeader @brandVersion={{brandVersion}}/>
  {{/if}}
  <div class="row align-center md:mb-sm period-container">
    {{#if monthlyPlansAvailable}}
      <RadioButton
          @classNames="px-xs md:px-sm ttext-page"
          @checkedClass="active"
          @value={{1}}
          @groupValue={{monthsPerCycle}}>
        <span>{{t 'wizard.bundles.cycle.1_month'}}</span>
      </RadioButton>
    {{/if}}

    {{#if annualPlansAvailable}}
      <RadioButton
          @classNames="px-xs md:px-sm ttext-page"
          @checkedClass="active"
          @value={{12}}
          @groupValue={{monthsPerCycle}}>
        <span>{{t 'wizard.bundles.cycle.12_months'}}</span>
        <span class="tbg-green save-up ml-xs ttext-small white bold {{if this.timer.currentHoliday  this.timer.currentHoliday.bundlesTabsBg}}">
          {{#if yearlyDiscountTest}}
            {{t 'wizard.bundles.cycle.save_50'}}
          {{else}}
            {{t 'wizard.bundles.cycle.save_70'}}
          {{/if}}
        </span>
      </RadioButton>
    {{/if}}

    <RadioButton
        @classNames="px-xs md:px-sm ttext-page"
        @checkedClass="active"
        @value={{24}}
        @groupValue={{monthsPerCycle}}>
      <span>{{t 'wizard.bundles.cycle.24_months'}}</span>
      <span class="tbg-green save-up ml-xs ttext-small white bold {{if this.timer.currentHoliday this.timer.currentHoliday.bundlesTabsBg}}">
        {{#if yearlyDiscountTest}}
            {{t 'wizard.bundles.cycle.save_70'}}
          {{else}}
            {{t 'wizard.bundles.cycle.save_75'}}
          {{/if}}

      </span>
    </RadioButton>
  </div>
  <div class="bundle-cards-container row align-center {{if freemiumBrand 'freemium'}}">
    {{#each sortedModel as |bundle|}}
      <Bundles::BundleCard @bundle={{bundle}}
                           @action={{action 'chooseBundle'}}
                           @currentHoliday={{this.timer.currentHoliday}}
                           @isInNoLetterheadTest={{isInNoLetterheadTest}}/>
    {{/each}}
  </div>

  <div class="text-center my-sm">
    <div role="button" {{action 'cancelAnytimeInfo'}}>
      {{t 'wizard.bundles.cancel_anytime'}}
      <span class="pl-xs info-button icon-info-circle"></span>
    </div>
    <div class="flex-center mt-xs">
      {{#if back}}
        <button class="tbtn back red-hover" {{action "back"}} data-testing-id="back" type="button">{{t 'back'}}</button>
      {{else if (and upgrade freemiumBrand)}}
        <LinkTo @route="brands.brand-version.home" @model={{router.currentRoute.queryParams.brandVersionId}} data-testing-id="back" class="tbtn back red-hover">
          {{t 'back'}}
        </LinkTo>
      {{/if}}
      {{#unless upgrade}}
        <CurrencySelectionDropdown/>
      {{/unless}}
    </div>

  </div>

  {{#if (not freemiumBrand)}}
    <div class="text-center my-sm">{{t 'wizard.bundles.logo_forever'}}</div>
  {{/if}}

  <div class="bg-white flex-center flex-col py-sm">
    <img src="/images/wizard/bundles/security_badge.svg" class="security-badge mb-xs" alt={{t 'security_badge'}}>
    {{t 'wizard.bundles.ssl_payments'}}
    {{t 'wizard.bundles.ssl_payments_description'}}
  </div>

  <div class="flex flex-col items-center my-sm">
    {{t 'wizard.bundles.payment_methods'}}
    <div class="flex flex-wrap justify-center mx-sm mt-sm">
      <img src="/images/wizard/bundles/visa.png" class="payment-method" alt={{t 'visa'}}>
      <img src="/images/wizard/bundles/amex.png" class="payment-method" alt={{t 'amex'}}>
      <img src="/images/wizard/bundles/mastercard.png" class="payment-method" alt={{t 'mastercard'}}>
      <img src="/images/wizard/bundles/dinersclub.png" class="payment-method" alt={{t 'dinersclub'}}>
      <img src="/images/wizard/bundles/jbc.png" class="payment-method" alt={{t 'jbc'}}>
      <img src="/images/wizard/bundles/discover.png" class="payment-method" alt={{t 'discover'}}>
      <img src="/images/wizard/bundles/paypal.png" class="payment-method" alt={{t 'paypal'}}>
    </div>
  </div>

  <div class="bg-white flex justify-center">
    <div class="faqs-container flex justify-start">
      <FrequentlyAskedQuestions @questionSelected={{if freemiumBrand null 'why_subscription'}}/>
    </div>
  </div>

  <div class="row py-sm recommendations-container">
    <div class="columns small-12 medium-6 large-4 text-center mb-sm md:mb-0">
      <img src="/images/wizard/bundles/person1.png" class="mb-xs" alt={{t 'person'}}>
      <p class="ttext-small">{{t 'wizard.bundles.testimonials.text1'}}</p>
      <div class="mt-xs">{{t 'wizard.bundles.testimonials.bottom1'}}</div>
      <div class="mt-xs">
        {{#each (range 0 5)}}
          <i class="icon-star star"></i>
        {{/each}}
      </div>
    </div>
    <div class="columns small-12 medium-6 large-4 text-center mb-sm md:mb-0">
      <img src="/images/wizard/bundles/person2.png" class="mb-xs" alt={{t 'person'}}>
      <p class="ttext-small">{{t 'wizard.bundles.testimonials.text2'}}</p>
      <div class="mt-xs">{{t 'wizard.bundles.testimonials.bottom2'}}</div>
      <div class="mt-xs">
        {{#each (range 0 5)}}
          <i class="icon-star star"></i>
        {{/each}}
      </div>
    </div>
    <div class="columns small-12 hide-for-medium-only large-4 text-center">
      <img src="/images/wizard/bundles/person3.png" class="mb-xs" alt={{t 'person'}}>
      <p class="ttext-small">{{t 'wizard.bundles.testimonials.text3'}}</p>
      <div class="mt-xs">{{t 'wizard.bundles.testimonials.bottom3'}}</div>
      <div class="mt-xs">
        {{#each (range 0 5)}}
          <i class="icon-star star"></i>
        {{/each}}
      </div>
    </div>
  </div>

  <div class="bg-white flex-center flex-col text-center py-sm">
    {{t 'wizard.bundles.example_brands'}}
    <div class="flex flex-wrap justify-center my-md">
      <img src="/images/wizard/bundles/logo1.png" class="brand" alt={{t 'logo'}}>
