{{#if (eq cardType 'PREMIUM_BRANDS')}}
{{#each model as |purchased|}}
  <CardComponent @imgSrc={{purchased.primaryBrandVersion.mockupUrl}} @title={{purchased.primaryBrandVersion.dateCreated}}>
    <LinkTo @route="brands.brand-version"
            @model={{purchased.primaryBrandVersion.id}}
            class="tbtn blue rounded-full py-xs px-sm"
            data-testing-id={{purchased.primaryBrandVersion.id}}>
      {{t "select"}}
    </LinkTo>
  </CardComponent>
{{/each}}

{{else if (eq cardType 'FREE_BRANDS')}}
  {{#each model as |free|}}
    <CardComponent @imgSrc={{free.freemiumPrimaryBrandVersion.mockupUrl}}
                   @title={{free.freemiumPrimaryBrandVersion.dateCreated}}>
      <LinkTo @route="brands.brand-version"
              @model={{free.freemiumPrimaryBrandVersion.id}}
              class="tbtn blue rounded-full py-xs px-sm"
              data-testing-id={{free.freemiumPrimaryBrandVersion.id}}>
        {{t "select"}}
      </LinkTo>
    </CardComponent>
  {{/each}}

{{else if (eq cardType 'DRAFT_BRANDS')}}
  {{#each model as |brand|}}
    <CardComponent @imgSrc={{brand.brandVersion.mockupUrl}} @title={{brand.dateCreated}}>
      <div class="brand-actions flex" data-testing-id={{brand.brandVersion.id}}>
        <LinkTo @route="wizard.editor"
                @model={{brand.businessId}}
                @query={{hash currentId=brand.brandVersion.id
                              originalIndex=brand.slotIndex
                              backTo='saved-brands'
                              logosStepId=brand.logosWizardStepId}}
                data-testing-id="edit-brand-link"
                class="tbtn pencil blue outline rounded-full">
