app/pods/components/editor-controls/temp.source:
{{#if genieAsset}}
  <LinkTo @route={{concat linksBase '.upload'}}
          @query={{hash asset_id=genieAsset.id}}
          data-testing-id="upload"
          class="menu-button">
    <i class="icon-picture-o" aria-hidden="true"></i>
    <div class="tool-label">{{t 'genie.editor_controls.image'}}</div>
  </LinkTo>

  <LinkTo @route={{concat linksBase '.layout'}}
          @query={{hash asset_id=genieAsset.id}}
          data-testing-id="layout"
          class="menu-button">
    <i class="icon-object-group" aria-hidden="true"></i>
    <div class="tool-label">{{t 'genie.editor_controls.layout'}}</div>
  </LinkTo>
{{else if brandVersion}}
  {{#unless isRedesignFontTabTest}}
    <LinkTo @route={{concat linksBase '.typeface'}}
            @current-when={{concat linksBase '.typeface'}}
            @query={{hash currentId=brandVersion.id}}
            data-testing-id="typeface-link"
            class="menu-button"
            @invokeAction={{action "reportHeapEvent" "typeface"}}>
      <i class="icon-font" aria-hidden="true"></i>
      <div class="tool-label">{{t 'wizard.edit.controls.typography_short'}}</div>
    </LinkTo>
  {{/unless}}

  {{#if isRedesignFontTabTest }}
      <LinkTo @route={{concat linksBase '.typeface.font'}}
            @current-when={{concat linksBase '.typeface.font'}}
            @query={{hash currentId=brandVersion.id}}
            data-testing-id="typeface-link"
            class="menu-button"
            @invokeAction={{action "reportHeapEvent" "typeface"}}>
      <i class="icon-font" aria-hidden="true"></i>
      <div class="tool-label">{{t 'wizard.edit.controls.typeface'}}</div>
    </LinkTo>

    <LinkTo @route={{concat linksBase '.typeface.text'}}
            @query={{hash currentId=brandVersion.id}}
            data-testing-id="edit-link"
            class="menu-button edit-button-fonts-test"
            @invokeAction={{action 'reportHeapEvent' 'edit'}}>
      <i class="icon-font-size" aria-hidden="true"></i>
      <div class="tool-label">{{t 'wizard.edit.controls.text'}}</div>
    </LinkTo>
  {{/if}}

  {{#if brandVersion.hasShapes}}
    <LinkTo @route={{concat linksBase '.shape'}}
            @query={{hash currentId=brandVersion.id}}
            data-testing-id="shapes-link"
            class="menu-button"
            @invokeAction={{action "reportHeapEvent" "shapes"}}>
      <i class="icon-circle-thin" aria-hidden="true"></i>
      <div class="tool-label">{{t 'wizard.edit.controls.shape'}}</div>
    </LinkTo>
  {{/if}}

  <LinkTo @route={{concat linksBase '.color'}}
          @query={{hash currentId=brandVersion.id}}
           data-testing-id="colors-link"
          class="menu-button"
          @invokeAction={{action "reportHeapEvent" "color"}}>
    <i class="icon-tint" aria-hidden="true"></i>
    <div class="tool-label">{{t 'wizard.edit.controls.color'}}</div>
  </LinkTo>

  {{#if brandVersion.hasLayouts}}
    <LinkTo @route={{concat linksBase '.layout'}}
            @query={{hash currentId=brandVersion.id}}
            data-testing-id="layouts-link"
            class="menu-button"
            @invokeAction={{action "reportHeapEvent" "layouts"}}>
      <i class="icon-object-group" aria-hidden="true"></i>
      <div class="tool-label">{{t 'wizard.edit.controls.layout'}}</div>
    </LinkTo>
  {{/if}}

  {{#if (or brandVersion.brand.iconBased brandVersion.brand.abstractBased)}}
    <LinkTo @route={{concat linksBase '.icon'}}
            @query={{hash currentId=brandVersion.id}}
            data-testing-id="icon-link"
            class="menu-button"
            @invokeAction={{action "reportHeapEvent" "icon"}}>
      <i class="icon-diamond" aria-hidden="true"></i>
      <div class="tool-label">{{t 'wizard.edit.controls.icon'}}</div>
    </LinkTo>
  {{/if}}

  {{#if showSavedBrands}}
    <div class="saved-container">
      <LinkTo @route={{concat linksBase '.saved'}}
              @query={{hash currentId=brandVersion.id}}
              data-testing-id="saved-brands-link"
              class="menu-button"
              @invokeAction={{action "reportHeapEvent" "saved"}}>
        <i class="icon-heart" aria-hidden="true"></i>
        <div class="tool-label">{{t 'wizard.edit.controls.saved'}}</div>
      </LinkTo>
      {{#if (and isActionBarTest showSavedBubble)}}
        <div class="saved-bubble flex items-center justify-between">
          <div class="arrow"/>
          <i class="icon-heart"></i>
          <span>{{t 'wizard.edit.controls.saved_bubble'}}</span>
          <button class="icon-close1" {{action 'closeSavedBubble'}} type="button"></button>
        </div>
      {{/if}}
    </div>
  {{/if}}
{{/if}}

<a class="menu-button hide-for-small-only" role="button" {{action
