app/pods/components/editor-text/template.source:
{{#if model.initialBased}}
  <div class="editor-tweak-wrapper">
    <div class="editor-tweak initials">
      <div class="flex items-center">
        <div class="mr-xs">{{t 'wizard.edit.tweaks.initials.title'}}:</div>
        {{#unless validInitials}}
          <ul class="invalid-input">
            {{t 'wizard.edit.tweaks.initials.invalid_input'}}
          </ul>
        {{/unless}}
        {{#each initials as |initial|}}
          <Input @value={{initial.value}} class="editor-input text-center mr-xs edit-initials-input" @maxlength="3" data-testing-id="initials-input" />
        {{/each}}
        <button class="editor-button {{if validInitials 'active' 'disabled'}}" {{action "saveInitials"}}
                data-testing-id="save-initials-button" type="button">
          {{t 'save'}}
        </button>
      </div>
    </div>

    {{#if model.t1000}}
      <div class="editor-tweak initial-size">
        <div class="flex-center flex-col">
          <div class="text-left w-full">{{t 'wizard.edit.tweaks.initials.size'}}: {{model.initialTweakSize}}</div>
          <div class="flex-center my-xs w-full">
            <i class="icon-minus clickable" role="button" {{action 'changeSlider' 'initialTweakSize' -0.1 0.1 4}}></i>
            <Input @type="range" @id="initial-size-slider"
                   @value={{model.initialTweakSize}} @min={{0.1}} @max={{4}}
                   @step={{0.1}} @change={{action "sliderChanged" "initialTweakSize"}}
                   class="flex-grow-1 mx-sm"/>
            <i class="icon-plus clickable" role="button" {{action 'changeSlider' 'initialTweakSize' 0.1 0.1 4}}></i>
          </div>
        </div>
      </div>
    {{/if}}
  </div>
{{/if}}

{{#if (or model.capitalizationModifiableText model.t1000) }}
  <div class="editor-tweak-wrapper">
    {{#if model.capitalizationModifiableText}}
      <div class="editor-tweak text-capitalization">
        <div class="flex items-center justify-between">
          <div class={{if (not (eq intl.locale 'en')) 'title-short'}}>{{t 'wizard.edit.tweaks.caps.title_text'}}:</div>
          <button class="editor-button capitalization-button m-xs"
            {{action 'toggleMenu'}} data-testing-id="text-capitalization-menu" type="button">
            <span>{{t (concat 'wizard.edit.tweaks.caps.' (if model.maintainCapitalizationText 'manual' 'capitalized'))}}</span>
            <i class="icon-angle-down ml-xs"></i>
          </button>
          {{#if capsMenuOpen}}
            <div class="overlay" role="button" {{action 'toggleMenu'}}></div>
            <div class="drop-menu">
              <button class="menu-button" data-testing-id="text-capitalization-manual" {{action 'onCapsText'}} type="button">{{t 'wizard.edit.tweaks.caps.manual'}}</button>
              <button class="menu-button" data-testing-id="text-capitalization-capitalized" {{action 'offCapsText'}} type="button">{{t 'wizard.edit.tweaks.caps.capitalized'}}</button>
            </div>
          {{/if}}
        </div>
      </div>
    {{/if}}

    {{#if model.t1000}}
      <div class="editor-tweak text-kerning">
        <div class="flex-center flex-col">
          <div class="text-left w-full">{{t 'wizard.edit.tweaks.name.kerning'}}: {{model.textKerningFactor}}</div>
          <div class="flex-center my-xs w-full">
            <i class="icon-minus clickable" role="button" {{action 'changeSlider' 'textKerningFactor' -0.1}}></i>
            <Input @type="range" @id="text-kerning-slider" @value={{model.textKerningFactor}} @min={{-1}} @max={{5}} @step={{0.1}} @change={{action "sliderChanged" "textKerningFactor"}} class="flex-grow-1 mx-sm" />
            <i class="icon-plus clickable" role="button" {{action 'changeSlider' 'textKerningFactor' 0.1}}></i>
          </div>
        </div>
      </div>
    {{/if}}
  </div>
{{/if}}

<div class="editor-tweak-wrapper">
  <div class="editor-tweak tagline-text">
    <div class="flex flex-col justify-center">
      <div class="pb-xs">{{t 'wizard.edit.tweaks.tagline.text_title'}}:</div>
      <div class="flex">
        {{#unless validTagline}}
          <div class="invalid-input">
            {{#if invalidTaglineCharacters}}
              {{t 'wizard.edit.tweaks.tagline.invalid_input_characters' character=invalidTaglineCharacters}}
            {{else}}
              {{t 'wizard.edit.tweaks.tagline.invalid_input'}}
            {{/if}}
          </div>
        {{/unless}}
        <Input @value={{tagline}} class="editor-input flex-grow-1" placeholder={{t "wizard.edit.tweaks.tagline.placeholder"}} @enter={{action "saveTagline"}} @maxlength="30" data-testing-id="tagline-input" />
        <button class="editor-button typeface-button {{if validTagline 'active' 'disabled'}}" {{action "saveTagline"}}
                data-testing-id="save-tagline-button" type="button">
          {{t 'save'}}
        </button>
      </div>
    </div>
  </div>

  {{#if model.hasTagline}}
    <div class="editor-tweak tagline">
      <div class="flex-center flex-col">
        <div class="text-left w-full">{{t 'wizard.edit.tweaks.tagline.title'}}: {{model.taglineTweakSize}}</div>
        <div class="flex-center my-xs w-full">
          <i class="icon-minus clickable" role="button" {{action 'changeSlider' 'taglineTweakSize' -0.1 0.1 4}}></i>
          <Input @type="range" @id="tagline-size-slider" @value={{model.taglineTweakSize}} @min={{0.1}} @max={{4}} @step={{0.1}} @change={{action "sliderChanged" "taglineTweakSize"}} class="flex-grow-1 mx-sm" />
          <i class="icon-plus clickable" role="button" {{action 'changeSlider' 'taglineTweakSize' 0.1 0.1 4}}></i>
        </div>
      </div>
    </div>

    {{#if model.t1000}}
      <div class="editor-tweak tagline-kerning">
        <div class="flex-center flex-col">
          <div class="text-left w-full">{{t 'wizard.edit.tweaks.tagline.kerning'}}
            : {{model.taglineKerningFactor}}</div>
          <div class="flex-center my-xs w-full">
            <i class="icon-minus clickable" role="button" {{action 'changeSlider' 'taglineKerningFactor' -0.1}}></i>
            <Input @type="range" @id="tagline-kerning-slider" @value={{model.taglineKerningFactor}} @min={{-1}} @max={{5}} @step={{0.1}} @change={{action "sliderChanged" "taglineKerningFactor"}} class="flex-grow-1 mx-sm" />
            <i class="icon-plus clickable" role="button" {{action 'changeSlider' 'taglineKerningFactor' 0.1}}></i>
          </div>
        </div>
      </div>
    {{/if}}
  {{/if}}
</div>

