app/pods/components/pagination-nav/templ.source:
{{#if (gt this.totalPages 1)}}
	<ul class="pagination text-center mt-sm" role="navigation" data-testing-id="pagination-nav">
		<li class="show-for-small-only pagination-previous {{if (eq this.page 1) 'disabled'}}">
			{{#if (eq this.page 1)}}
				{{t 'previous'}}
			{{else}}
				<a {{on "click" this.previousPage}} role="button" data-testing-id="previous-page">{{t 'previous'}}</a>
			{{/if}}
		</li>
		<li class="show-for-medium pagination-previous {{if (eq this.pageSet 0) 'disabled'}}">
			{{#if (eq this.pageSet 0)}}
				{{t 'previous'}}
			{{else}}
				<a {{on "click" this.previousPageSet}} role="button" data-testing-id="previous-page-set">{{t 'previous'}}</a>
			{{/if}}
		</li>
		{{#each this.pageLinks as |pageLink|}}
			{{#if pageLink.current}}
				<li class="current" data-testing-id="index-page-{{pageLink.index}}">{{pageLink.index}}</li>
			{{else}}
				<li><a {{on "click" (fn this.gotoPageIndex pageLink.index)}} role="button" data-testing-id="index-page-{{pageLink.index}}">{{pageLink.index}}</a></li>
