app/pods/components/subscription/templat.source:
{{#if (or loadSubscriptionInfo.isRunning loadDomainInfo.isRunning)}}
  <div class="relative w-full">
    <BouncingLoader />
  </div>
{{else}}
  <div class="flex-center small-3">
    <div class="logo-mockup" style="{{style}}" {{action 'goToBrandVersion' brand.primaryBrandVersionId}} role="button"></div>
  </div>
  <div class="flex small-9">
    <div class="subscription-details ttext-small mx-ms w-full">
      <div class="flex">
        <span class="bold mr-xs">{{t 'account_settings.subscription.name'}}</span>
        <span>{{business.name}}</span>
      </div>
      <div class="flex">
        <span class="bold mr-xs">{{t 'account_settings.subscription.plan'}}</span>
        <span>
        {{#if bundle.isPremiumBoost}}
          {{t 'wizard.bundles.premium.title'}}
        {{else}}
          {{plan.name}}
        {{/if}}
        </span>
      </div>
      <div class="flex">
        <span class="bold mr-xs">{{t 'account_settings.subscription.status'}}</span>
        <span>{{t (concat 'account_settings.subscription.subscription_status.' subscriptionStatus)}}</span>
      </div>
      <div class="subscription-actions flex justify-between">
        <div class="flex mb-xs md:mb-xs lg:mb-0">
          <span class="bold mr-xs">{{t 'account_settings.subscription.billing_cycle'}}</span>
          <span>{{t (concat 'account_settings.subscription.billing_cycles.' plan.monthsPerCycle)}}</span>
        </div>
        <div class="flex justify-center align-middle">
          {{#if (eq subscriptionStatus 'canceled')}}
            <button class="tbtn red-gradient rounded" {{action (perform reactivate)}} data-testing-id="reactivate-canceled" type="button">
              {{#if reactivate.isRunning}}
              <BouncingLoader @class="button white-dots" />
              {{/if}}
              {{t 'reactivate'}}
            </button>
          {{else if (eq subscriptionStatus 'not_active')}}
            <LinkTo @route="brands.plans" @query={{hash brandVersionId=brand.primaryBrandVersionId}} class="tbtn red-gradient rounded" data-testing-id="upgrade-subscription">
              {{t 'upgrade'}}
            </LinkTo>
          {{else}}
            <LinkTo @route="brands.plans" @query={{hash brandVersionId=brand.primaryBrandVersionId}} class="tbtn red-gradient rounded" data-testing-id="upgrade-subscription">
              {{t 'upgrade'}}
            </LinkTo>
            <button class="tbtn underline" {{action 'openModal'}} data-testing-id="deactivate-subscription" type="button">
              {{t 'deactivate'}}
            </button>
          {{/if}}
        </div>
      </div>
    </div>
  </div>
{{/if}}

