               @isCtaEditTest={{model.isCtaEditTest}}
               @selectedBrandVersionId={{selectedBrandVersionId}}
               @showMoreButton={{gt availableBucketsCount 0}}
               @currentlyGettingMoreLogos={{generateMoreLogos.isRunning}}
               @toEdit={{action 'toEdit'}}
               @toPlans={{action 'toPlans'}}
               @loadMoreLogos={{action "loadMoreLogos"}}
               @isInLogoTypesTest={{model.isInLogoTypesTest}}
               @generateLogosByType={{generateLogosByType}}/>
  {{#if (not model.isMobileLogosPageTest)}}
    <section class="logo-actions text-center pt-md pb-sm flex-center flex-col">
      <button class="tbtn {{if (not loaderToEdit) 'pencil'}} tailor-primary-gradient rounded-full"
              data-testing-id="edit-button-mobile-test-sm"
