                          @generateLogosByType={{generateLogosByType}}/>
      <PersonalizedMockups @selectedBrandVersion={{model.selectedBrandVersion}} @backTask={{perform backTask}} @tailorIndustry={{this.model.business.tailorIndustry}}/>
    </div>
  {{else}}
    <div class="new-layout flex">
      {{#if model.isInLogoTypesTest}}
        <div class="logos-wrapper">
          <div class="text-blue-editor text-center mt-xs">{{t "wizard.logos.title"}}:</div>
          <div class="logos-navigation flex justify-around">
            {{#each logoTypesTabs as |type|}}
              <button class="logo-nav-item {{if (eq currentTab type) 'active'}}" {{action 'switchTab' type}}
                      data-testing-id={{concat 'logo-type-tab-' type}} type="button">{{t
                      (concat "wizard.logos." type)}}</button>
            {{/each}}
          </div>
          <div class="logos-helper-text flex-center">
            {{t "wizard.logos.helper_text"}}
          </div>
          <div class="logos-list-by-type">
            {{#each brandVersionIds as |brandVersionId|}}
              <LogoCard @brandVersionId={{brandVersionId}}
                        @selectAction={{perform checkAndSelect}}
                        @isCtaEditTest={{model.isCtaEditTest}}
                        @selected={{eq (to-string selectedBrandVersionId) (to-string brandVersionId)}}
              />
            {{/each}}
            {{#if generateLogosByType.isRunning}}
              <div class="loading-logos">
                <BouncingLoader @class="gray-dots"/>
              </div>
            {{else if showLoadMoreBtn}}
              <SeeMoreLogos @currentlyGettingMoreLogos={{generateMoreLogos.isRunning}}
                            @loadMoreLogos={{action "loadMoreLogos"}}/>
            {{/if}}
          </div>
        </div>
      {{else}}
        <div class="logos-helper-text medium flex-center">
          {{t "wizard.logos.helper_text"}}
        </div>
        <div class="logos-list">
          {{#each model.logosWizardStep.brandVersionIds as |brandVersionId|}}
            <LogoCard @brandVersionId={{brandVersionId}}
                      @selectAction={{perform checkAndSelect}}
                      @isCtaEditTest={{model.isCtaEditTest}}
                      @selected={{eq (to-string selectedBrandVersionId) (to-string brandVersionId)}}
            />
          {{/each}}
          {{#if (gt availableBucketsCount 0)}}
            <SeeMoreLogos @currentlyGettingMoreLogos={{generateMoreLogos.isRunning}}
                          @loadMoreLogos={{action "loadMoreLogos"}}/>
          {{/if}}
        </div>
      {{/if}}
      <div class="previews row expanded">
        <div class="{{if model.isInLogoTypesTest 'flex-basis-100-percent'
                         'flex-basis-50-percent-lg-up flex-basis-100-percent-md-only'}}">
          <div class="mockup-container selected-logo flex flex-col align-spaced">
            <div class="flex-center logo-wrap m-xs">
              {{#if model.selectedBrandVersion.backgroundColorStyle}}
