                     style="{{model.selectedBrandVersion.mockupUrlStyle}} {{model.selectedBrandVersion.backgroundColorStyle}}"/>
              {{/if}}
            </div>
            <div class="actions flex-center mx-xs mb-xs">
              <button class="tbtn back red-hover"
                      data-testing-id="logos-back"
                {{action (perform backTask)}} type="button">
                {{t "back"}}
              </button>
              <button
                      class="tbtn {{if (not loaderToEdit) 'pencil'}} tailor-primary-gradient rounded-full"
                      data-testing-id="edit-button-lg"
                {{action "toEdit"}} type="button">
                {{#if loaderToEdit}}
