                {{else if model.isCtaEditTest}}
                  {{t "wizard.logos.edit_your_logo"}}
                {{else}}
                  {{t "wizard.logos.customize_your_logo"}}
                {{/if}}
              </button>
              <button class="tbtn check red outline red-shadow rounded-full"
                      data-testing-id="buy-button-lg"
                {{action "toPlans"}} type="button">
                {{t "wizard.logos.love_it"}}
              </button>
            </div>
          </div>
        </div>
        {{#if (not screenSizes.isSmall)}}
          <LogoMockup @type="social-cover" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="business-card" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="iphone-instagram" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="tags" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="book" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="bottle" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="cup" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="ipad" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="letter" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="notebook" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="shirt" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="iphone" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="bags" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="computer" @brandVersion={{model.selectedBrandVersion}}/>
          <LogoMockup @type="hanged-shirt" @brandVersion={{model.selectedBrandVersion}} @class="show-for-large"/>
        {{/if}}
      </div>
    </div>
  {{/if}}
</div>

