app/pods/components/editor/mobile/typefa.source:
{{! template-lint-disable no-invalid-interactive }}
<div class="mobile-tab-menu flex overflow-x-scroll" {{on "scroll" (fn this.editor.closeFontsTooltip)}}>
  <div class="edit-fonts flex-center separator">
    <button class="menu-item" {{on "click" openFontsModal}} type="button" data-testing-id="logo-editor-change-fonts">
      <div class="icon-wrapper"><i class="icon-text"/></div>
      {{t 'wizard.mobile_editor.fonts'}}
    </button>
  </div>
  {{#if this.brand.initialBased}}
    <div class="edit-initials flex-center separator">
      <button class="menu-item" {{on "click" (fn this.changeState 'initial_text')}} type="button" data-testing-id="logo-editor-change-initial">
        <div class="icon-wrapper"><i class="icon-pencil"/></div>
        {{t 'wizard.mobile_editor.initial_text'}}
      </button>
      {{#if this.brand.t1000}}
        <button class="menu-item" {{on "click" (fn this.changeState 'initial_size')}} type="button" data-testing-id="logo-editor-initial-size">
          <div class="icon-wrapper"><i class="icon-text-height"/></div>
          {{t 'wizard.mobile_editor.initial_size'}}
        </button>
      {{/if}}
    </div>
  {{/if}}
  <div class="edit-name flex-center separator">
    {{#if this.brand.capitalizationModifiableText}}
      <button class="menu-item" {{on "click" (fn this.changeState 'name_capitalization')}} type="button" data-testing-id="logo-editor-name-capitalizations">
        <div class="icon-wrapper"><i class="icon-font-case"/></div>
        {{t 'wizard.mobile_editor.name_capitalization'}}
      </button>
    {{/if}}
    {{#if this.brand.t1000}}
      <button class="menu-item" {{on "click" (fn this.changeState 'name_spacing')}} type="button" data-testing-id="logo-editor-name-spacing">
        <div class="icon-wrapper"><i class="icon-text-width"/></div>
        {{t 'wizard.mobile_editor.name_spacing'}}
      </button>
    {{/if}}
  </div>
  <div class="edit-tagline flex-center">
    <button class="menu-item" {{on "click" (fn this.changeState 'tagline_text')}} type="button" data-testing-id="logo-editor-tagline-text">
      <div class="icon-wrapper"><i class="icon-pencil"/></div>
      {{t 'wizard.mobile_editor.tagline_text'}}
    </button>
    {{#if this.brand.hasTagline}}
      {{#if this.brand.t1000}}
        <button class="menu-item" {{on "click" (fn this.changeState 'tagline_spacing')}} type="button" data-testing-id="logo-editor-tagline-spacing">
          <div class="icon-wrapper"><i class="icon-text-width"/></div>
          {{t 'wizard.mobile_editor.tagline_spacing'}}
        </button>
      {{/if}}
      <button class="menu-item" {{on "click" (fn this.changeState 'tagline_size')}} type="button" data-testing-id="logo-editor-tagline-size">
        <div class="icon-wrapper"><i class="icon-text-height"/></div>
        {{t 'wizard.mobile_editor.tagline_size'}}
      </button>
    {{/if}}
  </div>
</div>

